import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import courses from '../data/courseData';
import './CourseDetail.css';

const CourseDetail = () => {
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    // Simulate API call with timeout
    const timer = setTimeout(() => {
      const foundCourse = courses.find(c => c.id === parseInt(id));
      setCourse(foundCourse);
      setLoading(false);
    }, 500);

    return () => clearTimeout(timer);
  }, [id]);

  if (loading) {
    return <div className="loading">Loading course details...</div>;
  }

  if (!course) {
    return <div className="error-message">Course not found</div>;
  }

  return (
    <div className="course-detail-container">
      <div className="course-header">
        <div className="course-header-content">
          <h1>{course.title}</h1>
          <p className="course-instructor">Instructor: {course.instructor}</p>
          <div className="course-meta-info">
            <span><i className="fas fa-clock"></i> {course.duration}</span>
            <span><i className="fas fa-book"></i> {course.lessons} lessons</span>
          </div>
        </div>
        <div className="course-header-image">
          <img src={course.image} alt={course.title} />
        </div>
      </div>

      <div className="course-content">
        <div className="course-description">
          <h2>Course Description</h2>
          <p>{course.description}</p>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam auctor, 
            nisl ac ultricies lacinia, nisl nisl aliquam nisl, eget aliquam nisl nisl 
            sit amet nisl. Nullam auctor, nisl ac ultricies lacinia, nisl nisl aliquam 
            nisl, eget aliquam nisl nisl sit amet nisl.
          </p>
        </div>

        <div className="course-lessons">
          <h2>Course Content</h2>
          <div className="lesson-list">
            {Array.from({ length: course.lessons }, (_, i) => (
              <div className="lesson-item" key={i}>
                <div className="lesson-number">{i + 1}</div>
                <div className="lesson-details">
                  <h3>Lesson {i + 1}: {i === 0 ? 'Introduction' : `Advanced Topic ${i}`}</h3>
                  <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                </div>
                <div className="lesson-duration">15 min</div>
              </div>
            ))}
          </div>
        </div>

        <div className="course-actions">
          <Link to="/register" className="btn btn-primary">Enroll Now</Link>
          <Link to="/" className="btn btn-secondary">Back to Courses</Link>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
