import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Admin.css';

const Admin = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [courseStats, setCourseStats] = useState([]);
  const [statsLoading, setStatsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('users'); // 'users' or 'courseStats' or 'settings'

  const [allowlistText, setAllowlistText] = useState('');
  const [allowlistFile, setAllowlistFile] = useState(null);
  const [allowlistMsg, setAllowlistMsg] = useState(null);
  const [allowlistUploading, setAllowlistUploading] = useState(false);

  const [unlockThreshold, setUnlockThreshold] = useState(120);
  const [savingThreshold, setSavingThreshold] = useState(false);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get('/api/users');
        setUsers(res.data);
        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.msg || 'Failed to fetch users');
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  useEffect(() => {
    // Fetch course time statistics
    const fetchCourseStats = async () => {
      try {
        setStatsLoading(true);
        const res = await axios.get('/api/courses/stats');
        setCourseStats(res.data);
      } catch (err) {
        console.error('Failed to fetch course statistics:', err);
      } finally {
        setStatsLoading(false);
      }
    };

    if (activeTab === 'courseStats') {
      fetchCourseStats();
    }
  }, [activeTab]);

  useEffect(() => {
    const fetchThreshold = async () => {
      try {
        const res = await axios.get('/api/settings/unlock-threshold');
        setUnlockThreshold(Number(res.data?.value) || 120);
      } catch (e) {
        setUnlockThreshold(120);
      }
    };

    if (activeTab === 'settings') {
      fetchThreshold();
    }
  }, [activeTab]);

  const handleAllowlistTextChange = (e) => {
    setAllowlistText(e.target.value);
  };

  const handleAllowlistFileChange = (e) => {
    setAllowlistFile(e.target.files[0]);
  };

  const handleAllowlistUpload = async (e) => {
    e.preventDefault();
    setAllowlistUploading(true);
    setAllowlistMsg(null);
    try {
      let res;
      if (allowlistFile) {
        const formData = new FormData();
        formData.append('file', allowlistFile);
        res = await axios.post('/api/users/allowlist', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
      } else if (allowlistText.trim()) {
        res = await axios.post('/api/users/allowlist', { text: allowlistText });
      } else {
        setAllowlistMsg('Please provide text or upload a file.');
        setAllowlistUploading(false);
        return;
      }
      setAllowlistMsg(res.data.msg + (res.data.count ? ` (${res.data.count} entries)` : ''));
      setAllowlistText('');
      setAllowlistFile(null);
    } catch (err) {
      setAllowlistMsg(err.response?.data?.msg || 'Upload failed');
    }
    setAllowlistUploading(false);
  };

  // Format duration in seconds to mm:ss or hh:mm:ss
  const formatDuration = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const remainingSeconds = Math.floor(seconds % 60);

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
    } else {
      return `${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
    }
  };

  const saveThreshold = async (e) => {
    e.preventDefault();
    setSavingThreshold(true);
    try {
      const value = Number(unlockThreshold);
      if (!Number.isFinite(value) || value < 0) {
        alert('请输入非负整数秒数');
        setSavingThreshold(false);
        return;
      }
      const res = await axios.put('/api/settings/unlock-threshold', { value });
      window.__UNLOCK_THRESHOLD_SECONDS__ = Number(res.data?.value) || 120;
      alert('保存成功');
    } catch (e) {
      alert('保存失败');
    }
    setSavingThreshold(false);
  };

  if (loading) {
    return (
      <div className="admin-container">
        <h1>Admin Dashboard</h1>
        <div className="loading">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-container">
        <h1>Admin Dashboard</h1>
        <div className="error">Error: {error}</div>
      </div>
    );
  }

  return (
    <div className="admin-container">
      <h1>Admin Dashboard</h1>

      <div className="admin-tabs">
        <button className={`tab-button ${activeTab === 'users' ? 'active' : ''}`} onClick={() => setActiveTab('users')}>用户</button>
        <button className={`tab-button ${activeTab === 'courseStats' ? 'active' : ''}`} onClick={() => setActiveTab('courseStats')}>课程统计</button>
        <button className={`tab-button ${activeTab === 'settings' ? 'active' : ''}`} onClick={() => setActiveTab('settings')}>设置</button>
      </div>

      {activeTab === 'users' && (
        <div className="admin-card">
          <h2>用户列表</h2>
          <div className="user-table-container">
            <table className="user-table">
              <thead>
                <tr>
                  <th>姓名</th>
                  <th>邮箱</th>
                  <th>管理员</th>
                </tr>
              </thead>
              <tbody>
                {users.map((u) => (
                  <tr key={u._id}>
                    <td>{u.name}</td>
                    <td>{u.email}</td>
                    <td>{u.isAdmin ? '是' : '否'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {activeTab === 'courseStats' && (
        <div className="admin-card">
          <h2>课程学习统计</h2>
          {statsLoading ? (
            <div className="loading">Loading...</div>
          ) : (
            <div className="user-table-container stats-container">
              {courseStats.length === 0 ? (
                <div className="no-data">暂无数据</div>
              ) : (
                <table className="user-table stats-table">
                  <thead>
                    <tr>
                      <th>用户</th>
                      <th>邮箱</th>
                      <th>课程</th>
                      <th>总时长</th>
                      <th>访问次数</th>
                      <th>最近访问</th>
                    </tr>
                  </thead>
                  <tbody>
                    {courseStats.map((s, idx) => (
                      <tr key={idx}>
                        <td>{s.userName}</td>
                        <td>{s.userEmail}</td>
                        <td>{`${s.courseId} - ${s.courseName}`}</td>
                        <td>{formatDuration(s.totalDuration)}</td>
                        <td>{s.visits}</td>
                        <td>{new Date(s.lastVisit).toLocaleString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          )}
        </div>
      )}

      {activeTab === 'settings' && (
        <div className="admin-card">
          <h2>解锁阈值设置</h2>
          <form onSubmit={saveThreshold}>
            <label>上一门课需学习时间（秒）</label>
            <div style={{ marginTop: 8, marginBottom: 12 }}>
              <input
                type="number"
                min="0"
                step="1"
                value={unlockThreshold}
                onChange={(e) => setUnlockThreshold(e.target.value)}
                style={{ padding: 8, width: 200 }}
              />
            </div>
            <button className="btn" type="submit" disabled={savingThreshold}>
              {savingThreshold ? '保存中...' : '保存'}
            </button>
          </form>
          <div style={{ marginTop: 12, color: '#666' }}>
            当前全局阈值：{unlockThreshold} 秒
          </div>
        </div>
      )}
    </div>
  );
};

export default Admin;
