import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

// Mock course data - will be replaced with real data later
const courses = [
  {
    id: 1,
    title: 'Introduction to Web Development',
    description: 'Learn the basics of HTML, CSS and JavaScript to build modern websites.',
    image: 'https://via.placeholder.com/300x200?text=Web+Development'
  },
  {
    id: 2,
    title: 'Advanced JavaScript',
    description: 'Master JavaScript ES6+, promises, async/await and modern patterns.',
    image: 'https://via.placeholder.com/300x200?text=Advanced+JS'
  },
  {
    id: 3,
    title: 'React Fundamentals',
    description: 'Build interactive UIs with the most popular frontend library.',
    image: 'https://via.placeholder.com/300x200?text=React'
  },
  {
    id: 4,
    title: 'Backend Development with Node.js',
    description: 'Create robust server-side applications using Node.js and Express.',
    image: 'https://via.placeholder.com/300x200?text=Node.js'
  },
  {
    id: 5,
    title: 'Database Design with MongoDB',
    description: 'Learn to design and implement NoSQL databases for web applications.',
    image: 'https://via.placeholder.com/300x200?text=MongoDB'
  },
  {
    id: 6,
    title: 'Full Stack Development',
    description: 'Combine frontend and backend skills to build complete web applications.',
    image: 'https://via.placeholder.com/300x200?text=Full+Stack'
  },
  {
    id: 7,
    title: 'RESTful API Design',
    description: 'Design and implement scalable APIs following REST principles.',
    image: 'https://via.placeholder.com/300x200?text=API+Design'
  },
  {
    id: 8,
    title: 'Web Security Fundamentals',
    description: 'Protect your web applications from common security vulnerabilities.',
    image: 'https://via.placeholder.com/300x200?text=Web+Security'
  },
  {
    id: 9,
    title: 'Responsive Web Design',
    description: 'Create websites that work flawlessly across all devices and screen sizes.',
    image: 'https://via.placeholder.com/300x200?text=Responsive+Design'
  }
];

const Home = () => {
  // Function to chunk the courses into rows of 3
  const getRows = (courses, size) => {
    const result = [];
    for (let i = 0; i < courses.length; i += size) {
      result.push(courses.slice(i, i + size));
    }
    return result;
  };

  const courseRows = getRows(courses, 3);

  return (
    <div className="home-container">
      <h1>Course Tutorial Center</h1>
      <p>Browse our available tutorials to enhance your skills</p>

      <div className="course-grid">
        {courseRows.map((row, rowIndex) => (
          <div className="course-row" key={rowIndex}>
            {row.map(course => (
              <div className="course-card" key={course.id}>
                <img src={course.image} alt={course.title} className="course-image" />
                <div className="course-content">
                  <h3 className="course-title">{course.title}</h3>
                  <p className="course-description">{course.description}</p>
                  <Link to={`/courses/${course.id}`} className="btn">
                    View Tutorial
                  </Link>
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="auth-links">
        <Link to="/register" className="btn">
          Register
        </Link>
        <Link to="/login" className="btn">
          Login
        </Link>
      </div>
    </div>
  );
};

export default Home;
